import React, { useState } from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Features from './components/Features';
import ContactForm from './components/ContactForm';
import Footer from './components/Footer';
import './assets/css/index.css';

function App() {
  // State to control the visibility of the Industries section
  const [showIndustries, setShowIndustries] = useState(false);

  // Toggle function for Industries section
  const toggleIndustries = () => {
    setShowIndustries(!showIndustries);
  };

  return (
    <div className="App">
      <Navbar toggleIndustries={toggleIndustries} />
      <Hero />
      <Features />

      {/* Conditional rendering of Industries section */}
      {showIndustries && (
        <section id="industries" className="industries-section">
          <div className="container">
            <h2>Industries We Serve</h2>
            <p>Here are the industries we specialize in:</p>
            {/* Add content specific to industries */}
          </div>
        </section>
      )}

      <ContactForm />
      <Footer />
    </div>
  );
}

export default App;
